import React, { useState } from "react";
import { FaRobot, FaPaperPlane } from "react-icons/fa";

const Chatbot = () => {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]); // Stores chat history
  const [loading, setLoading] = useState(false);

  const handleAsk = async () => {
    if (!question.trim()) return;

    const newMessage = { sender: "user", text: question };
    setMessages([...messages, newMessage]);
    setQuestion("");
    setLoading(true);

    try {
      const res = await fetch("http://127.0.0.1:8000/ask-ai/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query: question }), // ‚úÖ Fix: Use "query" instead of "question"
      });

      const data = await res.json();
      const botMessage = { sender: "bot", text: data.answer || "‚ö†Ô∏è I couldn't find an answer." };
      setMessages((prevMessages) => [...prevMessages, botMessage]);
    } catch (error) {
      setMessages((prevMessages) => [...prevMessages, { sender: "bot", text: "‚ö†Ô∏è Error fetching response. Try again." }]);
    }

    setLoading(false);
  };

  return (
    <div className="flex flex-col h-screen bg-gray-950 text-white">
      {/* Header */}
      <div className="p-4 bg-gray-900 flex items-center justify-center shadow-md">
        <FaRobot className="text-yellow-400 text-3xl mr-3" />
        <h2 className="text-2xl font-semibold">AstraTax AI Chatbot</h2>
      </div>

      {/* Chat Messages */}
      <div className="flex-1 overflow-y-auto p-6 space-y-4">
        {messages.map((msg, index) => (
          <div key={index} className={`flex ${msg.sender === "user" ? "justify-end" : "justify-start"}`}>
            <div
              className={`p-3 max-w-3xl text-lg rounded-lg shadow-md ${
                msg.sender === "user" ? "bg-yellow-500 text-black" : "bg-gray-800 text-white"
              }`}
            >
              {msg.text}
            </div>
          </div>
        ))}
        {loading && <p className="text-yellow-400">Thinking... ü§ñ</p>}
      </div>

      {/* Input Box */}
      <div className="p-4 bg-gray-900 flex items-center border-t border-gray-700">
        <input
          type="text"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          className="flex-1 p-3 border border-gray-700 bg-gray-800 rounded-lg text-lg text-white focus:outline-none focus:ring-2 focus:ring-yellow-500"
          placeholder="Ask me a tax question..."
        />
        <button
          onClick={handleAsk}
          className="ml-3 bg-yellow-500 text-black p-3 rounded-lg transition hover:bg-yellow-600"
          disabled={loading}
        >
          <FaPaperPlane className="text-xl" />
        </button>
      </div>
    </div>
  );
};

export default Chatbot;
