import React, { useState } from "react";

const TaxForm = ({ onSubmit }) => {
  const [income, setIncome] = useState("");
  const [deductions, setDeductions] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ income, deductions });
  };

  return (
    <div className="p-6 bg-white shadow-md rounded-lg max-w-lg mx-auto">
      <h2 className="text-2xl font-bold mb-4">Tax Estimator</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700">Annual Income (₹)</label>
          <input
            type="number"
            className="w-full p-2 border border-gray-300 rounded-md"
            value={income}
            onChange={(e) => setIncome(e.target.value)}
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Deductions (₹)</label>
          <input
            type="number"
            className="w-full p-2 border border-gray-300 rounded-md"
            value={deductions}
            onChange={(e) => setDeductions(e.target.value)}
            required
          />
        </div>
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded-md w-full"
        >
          Calculate Tax
        </button>
      </form>
    </div>
  );
};

export default TaxForm;
