import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Navbar from "./components/navbar";
import HeroSection from "./components/hero";
import FeaturesSection from "./components/features";
import HowItWorks from "./components/howitworks";
import Testimonials from "./components/testimonials";
import Pricing from "./components/pricing";
import CTA from "./components/CTA";
import Footer from "./components/footer";
import TaxForm from "./components/taxform";
import AIChatbot from "./components/AIChatbot";

function HomePage({ handleTaxCalculation, taxResult }) {
  return (
    <div className="App">
      <Navbar />
      <HeroSection />
      <FeaturesSection />
      <HowItWorks />
      <Testimonials />
      <Pricing />
      <TaxForm onSubmit={handleTaxCalculation} />
      {taxResult && (
        <div className="text-center p-4 bg-gray-100 mt-6">
          <h2 className="text-xl font-semibold">Estimated Tax: â‚¹{taxResult.tax}</h2>
          <p className="mt-4 text-md font-medium">ðŸ’¡ Tax-saving Tips:</p>
          <p className="text-sm text-gray-700">{taxResult.suggestion}</p>
        </div>
      )}
      <CTA />
      <Footer />
    </div>
  );
}

function App() {
  const [taxResult, setTaxResult] = useState(null);

  const handleTaxCalculation = async (formData) => {
    try {
      const response = await fetch("http://127.0.0.1:8000/calculate-tax", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (!response.ok) throw new Error("Failed to calculate tax");

      const data = await response.json();
      setTaxResult(data);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<HomePage handleTaxCalculation={handleTaxCalculation} taxResult={taxResult} />} />
        <Route path="/ai-chatbot" element={<AIChatbot />} />
      </Routes>
    </Router>
  );
}

export default App;
