import React, { useState } from "react";
import Navbar from "./components/navbar";
import HeroSection from "./components/hero";
import FeaturesSection from "./components/features";
import HowItWorks from "./components/howitworks";
import Testimonials from "./components/testimonials";
import Pricing from "./components/pricing";
import CTA from "./components/CTA";
import Footer from "./components/footer";
import TaxForm from "./components/taxform"; // ✅ Import TaxForm
import "./App.css";

function App() {
  const [taxResult, setTaxResult] = useState(null);

  const handleTaxSubmit = async (data) => {
    console.log("Form Data:", data); // ✅ Debugging
    try {
      const response = await fetch("http://127.0.0.1:8000/calculate-tax", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      const result = await response.json();
      setTaxResult(result);
    } catch (error) {
      console.error("Error calculating tax:", error);
    }
  };

  return (
    <div className="App">
      <Navbar /> {/* ✅ Navigation Bar */}
      <HeroSection /> {/* ✅ Hero Section */}
      <FeaturesSection /> {/* ✅ Features Section */}
      <HowItWorks /> {/* ✅ How It Works Section */}
      <Testimonials /> {/* ✅ Testimonials Section */}
      <Pricing /> {/* ✅ Pricing Plans */}
      <CTA /> {/* ✅ Call to Action */}

      {/* ✅ Tax Form Section */}
      <div className="py-16 bg-gray-100 text-center">
        <h2 className="text-3xl font-bold mb-4">Estimate Your Tax</h2>
        <p className="text-lg mb-6">Enter your income details to calculate tax.</p>
        <TaxForm onSubmit={handleTaxSubmit} />
        {taxResult && (
          <div className="mt-4 p-4 bg-green-100 text-green-700 rounded-md">
            <h3 className="font-bold">Estimated Tax:</h3>
            <p>₹ {taxResult.tax}</p>
          </div>
        )}
      </div>

      <Footer /> {/* ✅ Footer */}
    </div>
  );
}

export default App;
