import React, { useState, useEffect } from "react";

const TaxForm = ({ onSubmit, extractedData }) => {
  const [income, setIncome] = useState("");
  const [deductions, setDeductions] = useState("");
  const [regime, setRegime] = useState("new");

  // âœ… Auto-fill extracted data when it changes
  useEffect(() => {
    if (extractedData) {
      setIncome(extractedData.income || "");
      setDeductions(extractedData.deductions || "");
    }
  }, [extractedData]);

  const handleSubmit = (event) => {
    event.preventDefault();
    onSubmit({ income: parseFloat(income), deductions: parseFloat(deductions), regime });
  };

  return (
    <form onSubmit={handleSubmit} className="p-6 bg-gray-900 rounded-lg shadow-md text-center">
      <h2 className="text-lg font-semibold text-white mb-3">ðŸ“Š Tax Calculator</h2>

      <div className="mb-4">
        <label className="block text-white">Income (â‚¹):</label>
        <input
          type="number"
          value={income}
          onChange={(e) => setIncome(e.target.value)}
          className="w-full p-2 rounded bg-gray-800 text-white"
          required
        />
      </div>

      <div className="mb-4">
        <label className="block text-white">Deductions (â‚¹):</label>
        <input
          type="number"
          value={deductions}
          onChange={(e) => setDeductions(e.target.value)}
          className="w-full p-2 rounded bg-gray-800 text-white"
          required
        />
      </div>

      <div className="mb-4">
        <label className="block text-white">Tax Regime:</label>
        <select
          value={regime}
          onChange={(e) => setRegime(e.target.value)}
          className="w-full p-2 rounded bg-gray-800 text-white"
        >
          <option value="new">New Regime</option>
          <option value="old">Old Regime</option>
        </select>
      </div>

      <button type="submit" className="bg-yellow-500 hover:bg-yellow-600 text-black font-bold py-2 px-4 rounded">
        Calculate Tax
      </button>
    </form>
  );
};

export default TaxForm;
