import React, { useState } from "react";
import { FaRobot, FaPaperPlane } from "react-icons/fa";

const Chatbot = () => {
  const [question, setQuestion] = useState("");
  const [response, setResponse] = useState("");
  const [loading, setLoading] = useState(false);

  const GOOGLE_AI_API_KEY = "YOUR_GOOGLE_AI_API_KEY"; // Replace with your actual key

  const handleAsk = async () => {
    if (!question.trim()) return;

    setLoading(true);
    setResponse("");

    try {
      const res = await fetch(
        `https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=${GOOGLE_AI_API_KEY}`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            contents: [{ role: "user", parts: [{ text: question }] }],
          }),
        }
      );

      const data = await res.json();
      setResponse(data.candidates?.[0]?.content?.parts?.[0]?.text || "‚ö†Ô∏è No response.");
    } catch (error) {
      setResponse("‚ö†Ô∏è Error fetching response. Try again.");
    }

    setLoading(false);
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-900 text-white">
      <div className="max-w-2xl w-full p-6 bg-gray-950 rounded-lg shadow-lg border border-gray-800">
        <div className="flex items-center space-x-3 mb-4">
          <FaRobot className="text-yellow-400 text-3xl" />
          <h2 className="text-2xl font-semibold">AstraTax AI Chatbot</h2>
        </div>
        <div className="relative">
          <input
            type="text"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            className="w-full p-3 pr-12 border border-gray-700 bg-gray-800 rounded-lg text-lg text-white focus:outline-none focus:ring-2 focus:ring-yellow-500"
            placeholder="Ask me a tax question..."
          />
          <button
            onClick={handleAsk}
            className="absolute right-3 top-3 bg-yellow-500 text-black p-2 rounded-lg transition hover:bg-yellow-600"
            disabled={loading}
          >
            <FaPaperPlane />
          </button>
        </div>
        {loading ? (
          <p className="mt-4 text-yellow-400">Thinking... ü§ñ</p>
        ) : (
          response && (
            <div className="mt-4 p-3 bg-gray-800 rounded-lg border border-gray-700 text-lg">
              {response}
            </div>
          )
        )}
      </div>
    </div>
  );
};

export default Chatbot;
