import React, { useState, useEffect, useRef } from "react";
import { FaRobot, FaPaperPlane, FaUser } from "react-icons/fa";

const Chatbot = () => {
  const [question, setQuestion] = useState("");
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const chatContainerRef = useRef(null);

  const GOOGLE_AI_API_KEY = "YOUR_GOOGLE_AI_API_KEY"; // Replace with your actual API key

  const handleAsk = async () => {
    if (!question.trim()) return;

    const userMessage = { text: question, sender: "user" };
    setMessages((prev) => [...prev, userMessage]);
    setQuestion("");
    setLoading(true);

    try {
      const res = await fetch(
        `https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=${GOOGLE_AI_API_KEY}`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            contents: [{ role: "user", parts: [{ text: question }] }],
          }),
        }
      );

      const data = await res.json();
      const botResponse = data.candidates?.[0]?.content?.parts?.[0]?.text || "⚠️ No response.";
      setMessages((prev) => [...prev, { text: botResponse, sender: "bot" }]);
    } catch (error) {
      setMessages((prev) => [...prev, { text: "⚠️ Error fetching response.", sender: "bot" }]);
    }

    setLoading(false);
  };

  useEffect(() => {
    chatContainerRef.current?.scrollTo(0, chatContainerRef.current.scrollHeight);
  }, [messages]);

  return (
    <div className="flex items-center justify-center h-screen bg-gray-900 text-white">
      <div className="max-w-3xl w-full h-[90vh] bg-gray-950 rounded-lg shadow-lg border border-gray-800 flex flex-col">
        {/* Chat Header */}
        <div className="bg-gray-800 p-4 text-center text-lg font-semibold flex items-center justify-center border-b border-gray-700">
          <FaRobot className="text-yellow-400 text-xl mr-2" />
          AstraTax AI Chatbot
        </div>

        {/* Chat Messages */}
        <div ref={chatContainerRef} className="flex-1 overflow-y-auto p-4 space-y-4">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`flex ${msg.sender === "user" ? "justify-end" : "justify-start"}`}
            >
              {msg.sender === "bot" && (
                <FaRobot className="text-yellow-400 text-2xl self-end mr-2" />
              )}
              <div
                className={`p-3 rounded-lg max-w-xs ${
                  msg.sender === "user"
                    ? "bg-blue-500 text-white self-end"
                    : "bg-gray-800 text-white"
                }`}
              >
                {msg.text}
              </div>
              {msg.sender === "user" && (
                <FaUser className="text-blue-400 text-2xl self-end ml-2" />
              )}
            </div>
          ))}

          {loading && (
            <div className="flex justify-start items-center space-x-2">
              <FaRobot className="text-yellow-400 text-2xl" />
              <div className="p-3 bg-gray-800 text-white rounded-lg max-w-xs animate-pulse">
                Typing...
              </div>
            </div>
          )}
        </div>

        {/* Input Box */}
        <div className="p-4 border-t border-gray-700 flex items-center space-x-2">
          <input
            type="text"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            className="flex-1 p-3 bg-gray-800 border border-gray-700 rounded-lg text-lg text-white focus:outline-none focus:ring-2 focus:ring-yellow-500"
            placeholder="Ask me a tax question..."
          />
          <button
            onClick={handleAsk}
            className="bg-yellow-500 text-black p-3 rounded-lg hover:bg-yellow-600 transition"
            disabled={loading}
          >
            <FaPaperPlane />
          </button>
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
